import 'package:flutter/material.dart';import 'package:flutter/services.dart' show rootBundle;import 'dart:convert';import 'package:get/get.dart';import 'package:quiz_project/utils/Constants.dart';import 'core/app_core.dart';import 'data/sources/remote/api_service.dart';import 'home/home_view.dart';Future<void> main() async {  WidgetsFlutterBinding.ensureInitialized();  await AppConfig.instance.loadConfig(); // تحميل التكوين قبل تشغيل التطبيق  // تهيئة ApiService لتكون متاحة للجميع  Get.put(ApiService());  runApp(MyApp());}class MyApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    return GetMaterialApp(      title: AppConfig.instance.appSettings.appName,      theme: ThemeData(        // ألوان الثيم الأساسية من AppConfig        colorScheme: ColorScheme.fromSeed(          seedColor: AppConfig.instance.themingSettings.primaryColor,          primary: AppConfig.instance.themingSettings.primaryColor,          secondary: AppConfig.instance.themingSettings.accentColor,          background: AppConfig.instance.themingSettings.backgroundColor,        ),        // تطبيق الخط الافتراضي على مستوى التطبيق        fontFamily: APP_FONT_FAMILY, // <--- تطبيق الخط هنا        useMaterial3: true,      ),      locale: const Locale('ar'),      fallbackLocale: const Locale('en', 'US'),      home: HomePage(),    );  }}// ... (HomePage code is unchanged from your provided snippet, so it's omitted here for brevity) ...